import React, { useRef, useEffect } from 'react';

const BackgroundVideo = ({ videoId }) => {
  const videoRef = useRef(null);

  useEffect(() => {
    const video = videoRef.current;

    if (video) {
      video.src = `https://www.youtube.com/embed/${videoId}?autoplay=1&loop=1&playlist=${videoId}`;
    }
  }, [videoId]);

  return (
    <section className="background-video">
      <div className="overlay"></div>
      <video ref={videoRef} autoPlay muted loop className="video">
        <p>Your browser doesn't support HTML5 video. Here is a <a href={`https://www.youtube.com/watch?v=${videoId}`} target="_blank" rel="noopener noreferrer">link to the video</a> instead.</p>
      </video>
    </section>
  );
};

export default BackgroundVideo;
